import { useContext } from "react";
import About from "./components/about/About";
import Contact from "./components/contact/Contact";
import Intro from "./components/intro/Intro";
import ProductList from "./components/productList/ProductList";

import Toggle from "./components/toggle/Toggle";
import { ThemeContext } from "./context";
import './app.css'

const App = () => {
  
  const theme = useContext(ThemeContext);
  const darkMode = theme.state.darkMode;
  return (
    <div
      style={{
        backgroundColor: darkMode ? "#222" : "white",
        color: darkMode && "white",
      }}
    >
     <div>
     <ul class="nav__links">
          <li class="nav__item">
            <a class="nav__link" href="#intro">Features</a>
          </li>
          <li class="nav__item">
            <a class="nav__link" href="#p">Operations</a>
          </li>
          <li class="nav__item">
            <a class="nav__link" href="#about">Testimonials</a>
          </li>
          <li class="nav__item">
            <a class="nav__link" href="#c">Testimonials</a>
          </li>
          
        </ul>
     </div>
     
      <Toggle />
      <Intro />
      <About />
      <ProductList />
      <Contact />

      <div className="right">
       <p>Copyright &copy;2022,<a  href="mayengemichael@gmail.com" target="_blank" rel="noreferrer"><span>Mchael Simba</span></a></p>
      </div>
    </div>
  );
};

export default App;
